implementation.field:
({
        _numBatches: 0,
        _self: this,
        onBeginUpdateBatch: function PT_O_onBeginUpdateBatch() {
          ++this._numBatches;
        },
        onEndUpdateBatch: function PT_O_onEndUpdateBatch() {
          if (!--this._numBatches)
            this._self._reloadView();
        },
        get wantAllDetails() { 
          return false;
        },
        onItemAdded: function PT_O_onItemAdded(bookmark, folder, index) {
