var index = -1;
if (this.mCurrentTab == aTab)
  index = this.mPanelContainer.selectedIndex;
else {
  // Find and locate the tab in our list.
  for (var i = 0; i < l; i++)
    if (this.mTabContainer.childNodes[i] == aTab)
      index = i;
}

