// Now select the new tab before nuking the old one.
var currentIndex = this.mPanelContainer.selectedIndex;

var newIndex = -1;
if (currentIndex > index)
  newIndex = currentIndex-1;
else if (currentIndex < index)
  newIndex = currentIndex;
else if (index > 0)
  newIndex = index-1;
else
  newIndex = index;

