if (!v && this.buttondown) { 
  this.buttondown = false;
  this._pendingActive = true;
} 
else {
  if (this._pendingActive) {
    this.buttondown = true;
    this._pendingActive = false;
  }
}

if (v)
  this.setAttribute("buttonover", "true");
else
  this.removeAttribute("buttonover");
